import { Component, Input } from "@angular/core";

@Component({
  selector: "app-restaurant-details",
  templateUrl: "./restaurant-details.component.html",
  styleUrl: "./restaurant-details.component.scss",
})
export class RestaurantDetailsComponent {
  @Input() rating: number = 4.3;
  @Input() reviews: number = 913;
  @Input() costForTwo: string = "₹200 for two";
  @Input() name: string = "Hotel Sai Nath & Sai Restaurant";
  @Input() tags: string[] = [
    "North Indian",
    "North Indian",
    "North Indian",
    "North Indian",
    "North Indian",
  ];
}
<app-navbar></app-navbar> <app-bread-crumb></app-bread-crumb>
<div class="restaurant-info-container">
  <div class="details">
    <div class="rating">
      <img src="assets/star.svg" alt="Star Icon" class="star-icon" />
      <span>4.3</span>
      <span>(913 ratings)</span>
      <span>• ₹200 for two</span>
    </div>
    <h1 class="restaurant-name">Hotel Sai Nath & Sai Restaurant</h1>
    <div class="tags">
      <span class="tag" *ngFor="let tag of tags">{{ tag }}</span>
    </div>
  </div>
</div>
