import { Component, NgZone, OnInit } from "@angular/core";
import { AngularFireAuth } from "@angular/fire/compat/auth";
import { AngularFirestore } from "@angular/fire/compat/firestore";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";

@Component({
  selector: "app-register-page",

  templateUrl: "./register-page.component.html",
  styleUrl: "./register-page.component.scss",
})
export class RegisterPageComponent implements OnInit {
  public registerForm!: FormGroup;
  date: any;
  d: any;
  constructor(
    // private apiService: ApiService,
    // private authService: AuthService,
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    public afAuth: AngularFireAuth,
    public ngZone: NgZone,
    private firestore: AngularFirestore
  ) {
    this.registerForm = this.formBuilder.group({
      email: ["", [Validators.required]],
      phone: ["", [Validators.required]],
      password: ["", [Validators.required]],
      address: ["", [Validators.required]],
    });
  }

  ngOnInit(): void {
    this.d = new Date();
    this.date = this.d.toISOString();
  }

  signUp() {
    // let fd = {
    //   authType: "Email",
    //   email: this.registerForm.value["email"],
    //   address: this.registerForm.value["address"],
    //   phone: this.registerForm.value["phone"],
    //   // uId: userCredential?.user.uid.toString(),
    //   signUpTime: this.date,
    // };
    // console.log("rgisterForm", fd);

    // return;
    this.afAuth
      .createUserWithEmailAndPassword(
        this.registerForm.value["email"],
        this.registerForm.value["password"]
      )
      .then((userCredential: any) => {
        // After successful registration, update user profile with fullName
        let fd = {
          authType: "Email",
          email: this.registerForm.value["email"],
          address: this.registerForm.value["address"],
          phone: this.registerForm.value["phone"],
          uId: userCredential?.user.uid.toString(),
          signUpTime: this.date,
        };
        this.firestore
          .collection("registeredUser")
          .doc(userCredential?.user.uid.toString())
          .set(fd)
          .then(() => {
            console.log("Data saved successfully!");
            this.registerForm.reset();
          })
          .catch((error) => {
            console.error("Error saving data: ", error);
            // this.toastr.error('Something Went Wrong! Try Again');
          });
      })
      .then(() => {
        // Do something on successful registration
        console.log(
          "User registered with fullName:",
          this.registerForm.value["username"]
        );
      })
      .catch((error) => {
        // Handle registration error
        console.error("Registration error:", error);
      });
  }
}
<div class="container-fluid">
  <div class="row align-items-center">
    <!-- Left Section -->
    <div class="col-md-6 mb-5 mb-md-0">
      <div class="container_width left_margin">
        <h1>Your go-to food delivery service</h1>
        <p class="container_width font">
          Ultimate food delivery, serving mouthwatering dishes to your doorstep
          instantly!
        </p>
        <form
          [formGroup]="registerForm"
          (ngSubmit)="signUp()"
          class="container_width"
        >
          <input
            type="email"
            class="form-input"
            placeholder="Enter your Email"
            formControlName="email"
          />
          <input
            type="email"
            class="form-input"
            placeholder="Enter your password"
            formControlName="password"
          />
          <input
            type="text"
            class="form-input"
            placeholder="Share your Phone number"
            formControlName="phone"
          />
          <div class="input-group mb-3">
            <input
              type="text"
              class="form-control"
              placeholder="Add your Address"
              aria-label="Add your Address"
              formControlName="address"
            />
            <span class="input-group-text bg-transparent imgBorder">
              <img
                src="../../assets/Images/location-arrow.png"
                alt="Location Icon"
                style="width: 20px; height: 20px; opacity: 0.6"
              />
            </span>
          </div>

          <button type="submit" class="btn btn-custom">Register now</button>
        </form>
        <!-- style="font-size: 0.8rem; color: #888" -->
        <p class="container_width mt-3">
          This site is protected by reCAPTCHA and the
          <a href="#" style="color: #aaa; text-decoration: underline"
            >Google Privacy Policy</a
          >
          and
          <a href="#" style="color: #aaa; text-decoration: underline"
            >Terms of Service</a
          >
          apply.
        </p>
      </div>
    </div>
    <!-- Right Section -->
    <div class="col-md-6 text-center position-relative">
      <div class="position-relative d-inline-block w-100">
        <img
          src="../../assets/Images/graphics.png"
          alt="Tumdum"
          class="img-fluid img_width"
        />
      </div>
    </div>
  </div>
</div>
